CREATE SEQUENCE FLEX_CATEGORY_ID_SEQ AS INTEGER START WITH 1234;
CREATE SEQUENCE FLEX_COMMUNITY_ID_SEQ AS INTEGER START WITH 1234;
CREATE SEQUENCE FLEX_POST_ID_SEQ AS INTEGER START WITH 1234;
CREATE SEQUENCE FLEX_USER_ID_SEQ AS INTEGER START WITH 1234; 
CREATE SEQUENCE FLEX_INDUSTRY_ID_SEQ AS INTEGER START WITH 1234;
CREATE SEQUENCE FLEX_COMPANY_ID_SEQ AS INTEGER START WITH 1234;
CREATE SEQUENCE FLEX_PRODUCT_SEQ AS INTEGER START WITH 1234;
CREATE SEQUENCE FLEX_CONTACT_ID_SEQ AS INTEGER START WITH 1234; 

CREATE TABLE FLEX_BOARD(POST_ID VARCHAR(16) NOT NULL,TITLE VARCHAR(50) NOT NULL,CONTENTS VARCHAR(255),REG_ID VARCHAR(20),REG_DATE VARCHAR(10),COMMUNITY_ID VARCHAR(16) NOT NULL,CONSTRAINT PK_FLEX_BOARD PRIMARY KEY(POST_ID));
CREATE TABLE FLEX_CATEGORY(CATEGORY_ID VARCHAR(16) NOT NULL,CATEGORY_NAME VARCHAR(50) NOT NULL,CATEGORY_DESC VARCHAR(100),REG_DATE VARCHAR(10),CONSTRAINT PK_FLEX_CATEGORY PRIMARY KEY(CATEGORY_ID));
CREATE TABLE FLEX_COMMUNITY(COMMUNITY_ID VARCHAR(16) NOT NULL,COMMUNITY_NAME VARCHAR(50) NOT NULL,COMMUNITY_DESC VARCHAR(100),CATEGORY_ID VARCHAR(16),REG_ID VARCHAR(20),REG_DATE VARCHAR(10),CONSTRAINT PK_FLEX_COMMUNITY PRIMARY KEY(COMMUNITY_ID),CONSTRAINT FK_FLEX_CATEGORY FOREIGN KEY(CATEGORY_ID) REFERENCES FLEX_CATEGORY(CATEGORY_ID));
CREATE TABLE FLEX_DEPT(DEPT_ID VARCHAR(16) NOT NULL,DEPT_NAME VARCHAR(20) NOT NULL,DEPT_LEVEL VARCHAR(1),DEPT_DESC VARCHAR(100),PARENT_DEPT VARCHAR(16),CONSTRAINT PK_FLEX_DEPT PRIMARY KEY(DEPT_ID));
CREATE TABLE FLEX_USER(USER_NAME VARCHAR(20) NOT NULL,EN_NAME VARCHAR(20),COMP_PHONE VARCHAR(20),PHONE VARCHAR(20),CELL_PHONE VARCHAR(20),COMPANY VARCHAR(20),JOB_POSITION VARCHAR(20),ASSIGNMENT VARCHAR(20),OFFICER_YN VARCHAR(1),FAX VARCHAR(20),ZIP_CODE VARCHAR(10),ADDRESS VARCHAR(100),COMP_ZIP_CODE VARCHAR(10),COMP_ADDRESS VARCHAR(100),EMAIL VARCHAR(30),USER_ID VARCHAR(16) NOT NULL,DEPT_ID VARCHAR(16) NOT NULL,PASSWORD VARCHAR(16) NOT NULL,AUTHORITY VARCHAR(20),CONSTRAINT PK_FLEX_USER PRIMARY KEY(USER_ID),CONSTRAINT FK_FLEX_DEPT FOREIGN KEY(DEPT_ID) REFERENCES FLEX_DEPT(DEPT_ID));
CREATE TABLE FLEX_GENRE(GENRE_ID VARCHAR(5) NOT NULL,NAME VARCHAR(50) NOT NULL,CONSTRAINT PK_FLEX_GENRE PRIMARY KEY(GENRE_ID));
CREATE TABLE FLEX_MOVIE(MOVIE_ID VARCHAR(50) NOT NULL,GENRE_ID VARCHAR(5) NOT NULL,TITLE VARCHAR(50) NOT NULL,DIRECTOR VARCHAR(50) NOT NULL,ACTORS VARCHAR(100) NOT NULL,RUNTIME NUMERIC(3),RELEASE_DATE DATE,TICKET_PRICE NUMERIC(6,2),POSTER_FILE VARCHAR(1000),NOW_PLAYING CHARACTER(1),FILE_REF_ID VARCHAR(50),CONSTRAINT PK_FLEX_MOVIE PRIMARY KEY(MOVIE_ID),CONSTRAINT FK_FLEX_MOVIE FOREIGN KEY(GENRE_ID) REFERENCES FLEX_GENRE(GENRE_ID));
CREATE TABLE FLEX_ATTACHED(ID VARCHAR(50) NOT NULL,NAME VARCHAR(255),FILE_SIZE NUMERIC(15),REF_ID VARCHAR(50) NOT NULL,CONSTRAINT PK_FLEX_ATTACHED PRIMARY KEY(ID));
CREATE TABLE FLEX_INDUSTRY(ID INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 7) NOT NULL PRIMARY KEY,NAME VARCHAR(50));
CREATE TABLE FLEX_COMPANY(ID INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 15) NOT NULL PRIMARY KEY,NAME VARCHAR(50),ADDRESS VARCHAR(50),CITY VARCHAR(50),STATE VARCHAR(20),ZIP VARCHAR(20),PHONE VARCHAR(50),INDUSTRY_ID INTEGER);
CREATE TABLE FLEX_PRODUCT(PRODUCT_ID INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 19) NOT NULL PRIMARY KEY,NAME VARCHAR(40),CATEGORY VARCHAR(40),IMAGE VARCHAR(40),PRICE DOUBLE,DESCRIPTION VARCHAR(255),QTY_IN_STOCK INTEGER);
CREATE TABLE FLEX_CONTACT(CONTACT_ID INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 30) NOT NULL PRIMARY KEY,FIRST_NAME VARCHAR(40),LAST_NAME VARCHAR(40),ADDRESS VARCHAR(40),CITY VARCHAR(40),STATE CHARACTER(5),ZIP VARCHAR(10),PHONE VARCHAR(40),EMAIL VARCHAR(40));


